craft.script:
function main() {
    var size = params.size;
    var length = params.length;
    var spork = params.spork;

    // handles minimum length
    if (length < 15) { length = 15; }

    // assembled fork
    return union(
        scoop(),
        handle()
    ).scale(size);

    // fork components
    function scoop() {
        var scoopHead, diff, prong, scoopEnd;

        // parts
        diff = cylinder({
            r: 8,
            h: 20
        });

        prong = cylinder({
            r1: 0.75,
            r2: 0.25,
            h: 10,
            center: true
        }).rotateX(93);

        // transformations
        // creates scoop base
        scoopHead =  intersection(
            sphere(10), 
            diff.rotateX(90).translate([0, 7, 14])
        );

        scoopHead = scoopHead.rotateY(180);

        // hollows out scoop
        scoopHead = difference(
            scoopHead, 
            scoopHead.translate([0, 0, 1])
        );

        // creates fork or spork prongs
        if (spork) { 
            prong = prong.translate([0, 8, -1.3]);
            prong = prong.scale([1.5, 1, 6]);
        }

        else { 
            prong = prong.translate([0, 2.5, -1.3]);
            prong = prong.scale([1, 1, 6]);
        }

        scoopHead = difference(
            scoopHead,
            prong,
            prong.translate([-2, 0, 0]),
            prong.translate([2, 0, 0])
        );

        // shaves off the sides -- spork to fork! 
        if (!spork) { prong = prong.scale([3, 2, 2]); }

        scoopHead = difference(
            scoopHead,
            prong.translate([-5, -3, 8]),
            prong.translate([5, -3, 8])
        );

        return scoopHead.translate([0, 0.5, 10.5]).rotateX(15);
    }

    function handle() {
        var curve, neck, h;

        // parts
        curve = cylinder({
            r: 8,
            h: 20
        }).rotateX(90).translate([0, 10, 14]);

        neck = intersection(
            sphere(10),
            curve
        );

        neckDiff = cube({
            size: [10, 40, 10],
            center: true
        });

        h = cylinder({
            r1: 1.5,
            r2: 2,
            h: length,
            fn: 6
        }).scale([1, 0.4, 1]);

        // transformations
        neck = neck.rotateY(180);

        // transition to handle              
        neck = neck.translate([0, -13, 1]);
        neck = rotate([155, 0, 180], neck);
        neck = difference(
            neck,
            neck.rotateX(-10).translate([0, 2, -3])
        );

        // handle positioning
        h = h.rotateX(95);
        h = h.translate([0, -12, 3]);

        h = union(h, neck);

        h = difference(
            h,
            neckDiff.translate([6, -25, 5]).rotateZ(1),
            neckDiff.translate([-6, -25, 5]).rotateZ(-1),
            sphere(4).scale([1, 2, 1]).translate([0, -14, 1.5]).rotateX(10),
            cube({
                size: [15, 10, 10],
                center: true
            }).translate([0, -18, -2]).rotateX(6)
        );

        return h;
    }
}
